import React, { useEffect, useState } from "react";
import axios from "axios";
import Navbar from "./components/Navbar/navbar";
import {
  LineChart,
  Line,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  Legend,
  ResponsiveContainer,
} from "recharts";

const data = [
  {
    product_id: 1,
    name: "Cookie - Oatmeal",
    stock: 92,
    selling_price: 141,
  },
  {
    product_id: 2,
    name: "Beer - Original Organic Lager",
    stock: 88,
    selling_price: 27,
  },
  {
    product_id: 3,
    name: "Sobe - Orange Carrot",
    stock: 85,
    selling_price: 176,
  },
  {
    product_id: 4,
    name: "Pepper - Chillies, Crushed",
    stock: 99,
    selling_price: 70,
  },
];

const App = () => {
  const [orders, getOrders] = useState([]);
  const url = "https://assessment.api.vweb.app/products";
  const getDetails = () => {
    axios
      .get(url)
      .then((response) => {
        getOrders(response.data);
      })
      .catch((error) => console.error(`Error: ${error}`));
  };

  useEffect(() => {
    getDetails();
  }, []);

  return (
    // <div>
    //   <Navbar />
    // </div>
    <div style={{ width: "1100px", height: "600px", backgroundColor: "white" }}>
      <ResponsiveContainer width="100%" height="100%">
        <BarChart width={730} height={250} data={data}>
          <CartesianGrid strokeDasharray="3 3" />
          <XAxis dataKey="name" />
          <YAxis />
          <Tooltip />
          <Legend />
          <Bar dataKey="stock" fill="#8884d8" />
          <Bar dataKey="selling_price" fill="#82ca9d" />
        </BarChart>
      </ResponsiveContainer>
    </div>
  );
};

export default App;
