import React, { useEffect, useState } from "react";
import axios from "axios";
import Navbar from "./components/Navbar/navbar";
import {
  LineChart,
  Line,
  XAxis,
  YAxis,
  AreaChart,
  Area,
  linearGradient,
  CartesianGrid,
  RadarChart,
  Radar,
  PolarRadiusAxis,
  PolarAngleAxis,
  Tooltip,
  BarChart,
  Bar,
  Legend,
  ResponsiveContainer,
} from "recharts";

const App = () => {
  const [products, setProducts] = useState([]);
  const [orders, setOrders] = useState([]);
  const url_products = "https://assessment.api.vweb.app/products";
  const url_orders = "https://assessment.api.vweb.app/orders";

  const filterOrders = () => {
    let allOrders = [];
    orders.forEach((order) => {
      let id = order.product_id;
      let qty = order.quantity;
      allOrders.push(id);
    });

    // allOrders = allOrders.filter(
    //   (v, i, a) => a.findIndex((v2) => v2.id === v.id) === i
    // );

    setOrders = [...new Set(allOrders)];

    let singleList = new Map();

    // allOrders.forEach((order) => {
    //   // singleList.set(order.id, order.qty);

    // });

    console.log("CHELSEAAAAAAAAAAA!!!!!");
    console.log(setOrders);
  };

  const getProducts = () => {
    axios
      .get(url_products)
      .then((response) => {
        setProducts(response.data);
      })
      .catch((error) => console.error(`Error: ${error}`));
  };
  const getOrders = () => {
    axios
      .get(url_orders)
      .then((response) => {
        setOrders(response.data);
      })
      .catch((error) => console.error(`Error: ${error}`));
  };

  useEffect(() => {
    getProducts();
    getOrders();
  }, []);

  return (
    <>
      <div style={{ width: "1100px", height: "600px" }}>
        <h2>Edvora</h2>
        {filterOrders()}
        <ResponsiveContainer width="100%" height="40%">
          <AreaChart
            width={730}
            height={250}
            data={products}
            margin={{ top: 20, right: 0, left: 0, bottom: 0 }}
          >
            <defs>
              <linearGradient id="71B679" x1="0" y1="0" x2="0" y2="1">
                <stop offset="5%" stopColor="#8884d8" stopOpacity={0.4} />
                <stop offset="95%" stopColor="#8884d8" stopOpacity={0} />
              </linearGradient>
              <linearGradient id="A87EBE" x1="0" y1="0" x2="0" y2="1">
                <stop offset="5%" stopColor="#82ca9d" stopOpacity={0.3} />
                <stop offset="95%" stopColor="#82ca9d" stopOpacity={0} />
              </linearGradient>
            </defs>
            <XAxis dataKey="name[0]" />
            <YAxis />
            <CartesianGrid strokeDasharray="3 3" />
            <Tooltip />
            <Area
              type="monotone"
              dataKey="stock"
              stroke="#8884d8"
              fillOpacity={1}
              fill="url(#71B679)"
            />
            <Area
              type="monotone"
              dataKey="selling_price"
              stroke="#82ca9d"
              fillOpacity={1}
              fill="url(#A87EBE)"
            />
          </AreaChart>
        </ResponsiveContainer>
      </div>
    </>
  );
};

export default App;
